const list=document.querySelectorAll(".tabs-list"),linkMore=document.querySelector(".footer__link-more"),cart=document.querySelector(".cart");list.forEach(t=>{t.addEventListener("click",()=>{list.forEach(t=>t.classList.remove("tabs-list-active")),t.classList.add("tabs-list-active")})}),linkMore.addEventListener("click",()=>{cart.classList.add("cart__popup")});